    checkContentControl: () => {
        const isConfigured = valCheck(localStorage.getItem(v.keyContentControl));

        //if(!isConfigured && d.adsFirstCounter() >= 10)
        if(!isConfigured) {
            const dialogData = JSON.stringify({
                params: `tagPage=${v.tagPage}&dialogType=${v.dialogContentControl}`,
                bgColor: `#${d.colorAlpha[80]}000000`,
                backStack: true
            });

            if(window.wv) window.wv.openDialog(dialogData, false);
            else pageBroadcast.postMessage({ action: v.broadcastOpenDialog, dialogData: dialogData });
        }
    },
    checkAvalabieDomains: async () => {
        if(v.tagPage === v.homePage) localStorage.removeItem(v.keyFont);
        v.defaultId = localStorage.getItem(v.keyFont) ?? "null";
        return await new Promise(async(resolve, reject) => {
            const listCheck = arrayDomainCheckByPage();
            const run = (percentageSuccess) => {
                const list = JSON.parse(JSON.stringify(d.fontButtonList));

                if(percentageSuccess <= 70) {
                    const dialogData = JSON.stringify({
                        params: `tagPage=${v.tagPage}&dialogType=${v.dialogShowDns}`,
                        bgColor: `#${d.colorAlpha[80]}000000`,
                        backStack: true
                    });
    
                    if(window.wv) window.wv.openDialog(dialogData, false);
                    else pageBroadcast.postMessage({ action: v.broadcastOpenDialog, dialogData: dialogData });
                }
                else if(v.tagPage === v.homePage) d.checkContentControl();
                v.defaultId = (v.defaultId !== "null") ? v.defaultId : (d.fontArray.filter((item, index) => (item?.done ?? false))[0]?.font ?? "vizer");
                list.map((item, index) => {
                    const isError = !d.fontArray.some(i => (i?.done ?? false) && item?.attrs.includes(i?.font)) && item?.attrs.includes(`data-action="${v.actionLoadDefaultCat}"`);

                    if(item.attrs.includes(`data-cat-id="${v.defaultId}"`)) {
                        item.class = item.class + " active";
                    }
                    if(isError) list.push(item);
                    if(item.class.includes("active")) array_move(list, index, v.tagPage === v.homePage ? 0 : 1);
                });

                d.fontButtonList = list.filter((x,i) => list.findLastIndex(y => y.text == x.text) == i);
                localStorage.setItem(v.keyFontArray, JSON.stringify(d.fontArray));
                localStorage.setItem(v.keyFont, v.defaultId);
                resolve();
            }

            if(isIngles()) run(100);
            else {
                if(v.defaultId !== "null") run(100);
                promiseAllList(listCheck)
                .then(async data => {
                    let percentageSuccess = 0;
                    
                    data.map(item => {
                        if(item.itemList === "done") percentageSuccess += item.percentage;
                    });
                    run(Math.round(percentageSuccess));
                })
                .catch(err => logs("aborted"));
            }
        });
    },
