    userDB: {
        myListStore: [],
        myListChannelsStore: [],
        myListGamesStore: [],
        topRatedStore: [],
        searchStore: [],
        lastViewedStore: [],
        itemStore: [],
        userSettingsStore: [],
        titleStore: [],

        myList: () => removeTerms(d.userDB.myListStore.getList),
        myListChannels: () => removeTerms(d.userDB.myListChannelsStore.getList),
        myListGames: () => removeTerms(d.userDB.myListGamesStore.getList),
        topRatedList: () => removeTerms(d.userDB.topRatedStore.getList),
        searchList: () => removeTerms(d.userDB.searchStore.getList),
        lastViewedList: () => removeTerms(d.userDB.lastViewedStore.getList),
        itemList: () => removeTerms(d.userDB.itemStore.getList),
        userSettingsList: () => removeTerms(d.userDB.userSettingsStore.getList),

        getList: (type, defaultList) => {
            try {
                switch(type) {
                    case v.storeMyList: return d.userDB.myList();
                    case v.storeMyListChannels: return d.userDB.myListChannels();
                    case v.storeMyListGames: return d.userDB.myListGames();
                    case v.storeTopRated: return d.userDB.topRatedList();
                    case v.storeSearch: return d.userDB.searchList();
                    case v.storeItem: return d.userDB.itemList();
                    default: return defaultList;
                }
            } catch(err) {logs(err);}
            return defaultList;
        },
        listCheck: (type) => {
            switch(type) {
                case v.storeCache:
                case v.storeLastViewed:
                case v.storeItem:
                case v.storeMyList:
                case v.storeMyListChannels:
                case v.storeMyListGames:
                case v.storeSearch:
                case v.storeTopRated:
                case v.storeUserSettings:
                case v.storeChannelLinks:
                case v.storeTitle: return true;
                default: return false;
            }
        },
        initializer: async() => {
            return new Promise(async(resolve, reject) => {
                d.userDB.myListStore = await new StorageData(v.storeMyList, true, 500);
                d.userDB.myListChannelsStore = await new StorageData(v.storeMyListChannels, true, 500);
                d.userDB.myListGamesStore = await new StorageData(v.storeMyListGames, true, 500);
                d.userDB.topRatedStore = await new StorageData(v.storeTopRated, false, 10);
                d.userDB.searchStore = await new StorageData(v.storeSearch, false, 20);
                d.userDB.lastViewedStore = await new StorageData(v.storeLastViewed, true);
                d.userDB.itemStore = await new StorageData(v.storeItem, false, 20);
                d.userDB.userSettingsStore = await new StorageData(v.storeUserSettings, true, 1);
                d.userDB.titleStore = await new StorageData(v.storeTitle, false, 1);
                if(window.wv && !window.location.protocol.includes("file:")) window.onbeforeunload = function(){
                    return 'Are you sure you want to leave?';
                };
                resolve();
            });
        },
        userSettingsInit: () => {
            const isExist =  objCheck(d.userDB.userSettingsList());
            const dialogData = JSON.stringify({
                params: `lang=${v.lang}&tagPage=${v.tagPage}&dialogType=${isUpdate() ? v.dialogShowUpdate : v.dialogShowIntro}`,
                bgColor: `#000000`,
                backStack: false
            });

            if(isUpdate() && v.tagPage !== v.dialogPage) {
                if(window.wv) window.wv.openDialog(dialogData, false);
                else pageBroadcast.postMessage({ action: v.broadcastOpenDialog, dialogData: dialogData });
            }
            else if(!isExist && v.tagPage !== v.dialogPage) {
                if(window.wv) window.wv.openDialog(dialogData, false);
                else pageBroadcast.postMessage({ action: v.broadcastOpenDialog, dialogData: dialogData });
            }
            return v.tagPage === v.dialogPage || isUpdate() || isExist;
        }
    },
