function encodeLetterToNumber(str) {
    const start = 96;
    const len = str.length;
    const out = [...str.toLowerCase()].reduce((out, char, index) => {
        const val = char.charCodeAt(0) - start
        const pow = Math.pow(26, len - index - 1);
        return out + val * pow
    }, 0)
    return out;
}
