function dateToTimestamp(strDate) {
    var datum = Date.parse(strDate);
    return datum/1000;
}
function dayExpire(t0, t1) {
    const diffInSeconds = Math.abs(new Date(t0) - new Date(t1 * 1000)) / 1000;
    return (Math.floor(diffInSeconds / 60 / 60 / 24) === 1) ? t.wednesday : (Math.floor(diffInSeconds / 60 / 60 / 24) === 0) ? t.today : t.in + Math.floor(diffInSeconds / 60 / 60 / 24) + t.days;
}
function dateFormat(date) {
    const year = new Intl.DateTimeFormat('en', { year: 'numeric' }).format(date);
    const month = new Intl.DateTimeFormat('en', { month: '2-digit' }).format(date);
    const day = new Intl.DateTimeFormat('en', { day: '2-digit' }).format(date);
    const hour = String(date.getHours()).padStart(2, "0");
    const minutes = String(date.getMinutes()).padStart(2, "0");

    return `${month}/${day}/${year} ${hour}:${minutes}:00`;
}
function startsWith(str, word) {
    return str.lastIndexOf(word, 0) === 0;
}
function setWithExpiry(key, value, ttl) {
    ttl = ttl.includes("h") ? ((parseInt(ttl.replace("h", ""))*60*60+0*60+0)*1000) : ttl.includes("m") ? (parseInt(ttl.replace("m", ""))*60000) : ttl.includes("d") ? (parseInt(ttl.replace("d", ""))*24*60*60*1000) : ttl;
	const now = new Date();
	const item = {
		value: value,
		expiry: now.getTime() + ttl,
	}
	localStorage.setItem(key, JSON.stringify(item));
}
function getWithExpiry(key) {
	const itemStr = localStorage.getItem(key);
	if (!itemStr) {
		return null
	}
	const item = JSON.parse(itemStr)
	const now = new Date();
	if (now.getTime() > item.expiry) {
		localStorage.removeItem(key);
		return null;
	}
	return item.value;
}
