function getDomain(hostName) {
    return new URL(hostName).hostname.match(/^(?:.*?\.)?([a-zA-Z0-9\-_]{3,}\.(?:\w{2,8}|\w{2,4}\.\w{2,4}))$/)[1];
}
function isBase64(str) {
    if (str ==='' || str.trim() ===''){ return false; }
    try {
        return btoa(atob(str)) == str;
    } catch (err) {
        return false;
    }
}
function collapseItens(data) {
    try {
        let itemList = [];
        let isArrayList = false;
    
        data.map(item => {
            if(isArray(item.itemList) && item.itemList.length > 0) item.itemList.map(item => itemList.push(item));
        });
        itemList = itemList.filter((value, index, self) => index === self.findIndex((t) => (t.itemTitle === value.itemTitle && t.itemYear === value.itemYear)));
        data.map(item => {
            if(isArray(item.itemList) && item.itemList.length > 0 && !isArrayList) {
                item.itemList = itemList;
                isArrayList = true;
            }
        });
    } catch (err) {}
    return data;
}
function userTotalAcessCounter(){
    const isReady = (sessionStorage.getItem(v.keyReady) ?? "null") !== "null";
    const counter = parseInt(localStorage.getItem(v.keyAccessCounter) ?? "0");
    const adsFirstCounter = d.adsFirstCounter();

    if(adsFirstCounter >= 10) {
        if(!isReady) {
            if(counter <= v.totalAccess) localStorage.setItem(v.keyAccessCounter, counter+1);
            sessionStorage.setItem(v.keyReady, "true");
        }
    }
}
function percentageUserAccess() {
    const percentage = Math.round(parseInt(localStorage.getItem(v.keyAccessCounter) ?? "0") / v.totalAccess * 100);

    return (percentage > 100) ? 100 : percentage;
}
