$.fn.eachToArray = function(data) {
    try {
        const list = [];
        const type = data?.type ?? -1;
        const val = data?.val ?? "";
    
        switch(type) {
            case v.typeAttr: {
                this.each(function() {
                    const el = $(this);
            
                    if(valCheck(el.attr(val))) list.push(el.attr(val));
                });
                break;
            }
            case v.typeText: {
                this.each(function() {
                    const el = $(this).text().replace(/[^\d.-]/g, '');
            
                    if(valCheck(el) && isNumeric(el)) list.push(el);
                });
                break;
            }
        }
        return list;
    } catch (err) {}
    return null;
};
Array.prototype.remove = function(val) {
    var index = this.indexOf(val); 
    if (index >= 0) this.splice(index, 1);
    return this;
};
Array.prototype.add = function(val) {
    if(!this.includes(val)) this.push(val);
};
Array.prototype.removeIndex = function(index) {
    var b = '';
    for (b in this) {
      if (this[b] === index) {
        this.splice(b, 1);
        break;
      }
    }
    return this;
};
Date.prototype.addDays = function(days) {
    this.setDate(this.getDate() + days);
    return this;
};
Date.prototype.addHours = function(hours) {
    this.setTime(this.getTime() + (hours*60*60*1000));
    return this;
};

